import React, { useState, useEffect } from 'react';
import GitHubService from '../api/GitHubService';
import '../styles/UserDetails.css';

const UserDetails = ({ username }) => {
  const [userDetails, setUserDetails] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        const userData = await GitHubService.getUserDetails(username);
        setUserDetails(userData);
      } catch (err) {
        setError('Failed to fetch user details');
        console.error(err);
      }
    };

    fetchUserDetails();
  }, [username]);

  if (error) {
    return <div className="error">{error}</div>;
  }

  if (!userDetails) {
    return null; // Pode renderizar uma mensagem de carregamento aqui
  }

  return (
    <div className="user-details">
      <h2>User Details for {username}</h2>
      <div className="user-info">
        <img src={userDetails.avatar_url} alt={`${username}'s avatar`} className="user-avatar" />
        <p><strong>Name:</strong> {userDetails.name}</p>
        <p><strong>Username:</strong> {userDetails.login}</p>
        <p><strong>Location:</strong> {userDetails.location}</p>
        <p><strong>Followers:</strong> {userDetails.followers}</p>
        <p><strong>Following:</strong> {userDetails.following}</p>
        <p><strong>Public Repos:</strong> {userDetails.public_repos}</p>
      </div>
    </div>
  );
};

export default UserDetails;
