import React, { useState, useEffect } from 'react';
import GitHubService from '../api/GitHubService';

const UserList = ({ onSelectUser }) => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const usersData = await GitHubService.getUsers();
        if (Array.isArray(usersData)) { // Check if the response is an array
          setUsers(usersData);
        } else {
          // Handle the case where usersData is not an array
          console.error('Expected an array of users, but got:', usersData);
        }
      } catch (err) {
        // Handle error state
      }
    };

    fetchUsers();
  }, []);

  // Conditional rendering to ensure users is an array
  return (
    <div className="user-list">
      {Array.isArray(users) && users.map(user => (
        <div key={user.id} onClick={() => onSelectUser(user.login)}>
          {user.login}
        </div>
      ))}
    </div>
  );
};

export default UserList;
