import React, { useState, useEffect } from 'react';
import GitHubService from '../api/GitHubService';
import '../styles/UserDetails.css';

const UserDetails = ({ username }) => {
  const [userDetails, setUserDetails] = useState(null);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    setIsLoading(true);
    const fetchUserDetails = async () => {
      try {
        const userData = await GitHubService.getUserDetails(username);
        setUserDetails(userData);
      } catch (err) {
        setError(err.message || 'Failed to fetch user details');
      } finally {
        setIsLoading(false);
      }
    };

    if (username) {
      fetchUserDetails();
    }
  }, [username]);

  if (isLoading) {
    return <div className="loading">Loading user details...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  if (!userDetails) {
    return <div className="loading">User details not available.</div>;
  }

  const { avatar_url, name, login, location, followers, following, public_repos } = userDetails;

  return (
    <section className="user-details">
      <h2>User Details for {username}</h2>
      <div className="user-info">
        <img src={avatar_url} alt={`Avatar of ${name || username}`} className="user-avatar" />
        <p><strong>Name:</strong> {name}</p>
        <p><strong>Username:</strong> {login}</p>
        <p><strong>Location:</strong> {location}</p>
        <p><strong>Followers:</strong> {followers}</p>
        <p><strong>Following:</strong> {following}</p>
        <p><strong>Public Repos:</strong> {public_repos}</p>
      </div>
    </section>

  );
};

export default UserDetails;
