import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import GitHubService from "../api/GitHubService";
import Pagination from "./Pagination";

import "../styles/UserList"


const UserList = () => {
  const [users, setUsers] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(10);

  const navigate = useNavigate();

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const usersData = await GitHubService.getUsers(currentPage);
        setUsers(usersData.items); // Supondo que os usuários estão em 'items'
        const totalItems = usersData.total; // Total de usuários
        const itemsPerPage = 10; // Número de itens por página
        setTotalPages(Math.ceil(totalItems / itemsPerPage));
      } catch (error) {
        // Manipule o erro aqui
        console.error(error);
      }
    };

    fetchUsers();
  }, [currentPage]); // Dependência: currentPage


  const handleSelectUser = (username) => {
    navigate(`/users/${username}`);
  };

  return (
    <div className="user-list">
      <div>
        {users.map((user) => (
          <div key={user.id} onClick={() => handleSelectUser(user.login)}>
            {user.login}
          </div>
        ))}
      </div>

      <div>
        <Pagination
          currentPage={currentPage}
          totalPages={totalPages}
          onPageChange={setCurrentPage}
        />
      </div>
    </div>
  );
};

export default UserList;
