// src/components/UserDetails.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import GitHubService from '../api/GitHubService';
import UserRepositories from './UserRepositories'; // This will be our new component for step 5
import UserList from './UserList';

const UserDetails = () => {
  const [userDetails, setUserDetails] = useState(null);
  const [error, setError] = useState('');
  const { username } = useParams();

  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        const userData = await GitHubService.getUserDetails(username);
        setUserDetails(userData);
      } catch (err) {
        setError(err.message || 'Failed to fetch user details');
      }
    };

    fetchUserDetails();
  }, [username]);

  if (error) {
    return <div className="error">{error}</div>;
  }

  if (!userDetails) {
    return <div>Loading...</div>;
  }

  return (
    <div className="user-details">
      <h1>User Details</h1>
      <p>ID: {userDetails.id}</p>
      <p>Login: {userDetails.login}</p>
      <p>Profile URL: {userDetails.html_url}</p>
      <p>Account Creation Date: {new Date(userDetails.created_at).toLocaleDateString()}</p>
      <UserRepositories username={username} />
      <UserList onSelectUser={username} />
    </div>
  );
};

export default UserDetails;
