// src/components/UserList.js
import React, { useState, useEffect } from 'react';
import GitHubService from '../api/GitHubService';
import Pagination from './Pagination'; // Assume this is your pagination component
import '../styles/UserList.css';

const UserList = ({ onSelectUser }) => {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        // Modify this to use your pagination logic (e.g., passing currentPage to the API)
        const { data, pageCount } = await GitHubService.getUsers(currentPage);
        setUsers(data);
        setTotalPages(pageCount);
      } catch (err) {
        setError('Failed to fetch users');
        console.error(err);
      }
    };

    fetchUsers();
  }, [currentPage]);

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="user-list">
      <h1>GitHub Users</h1>
      {users.map(user => (
        <div key={user.id} className="user-item" onClick={() => onSelectUser(user.login)}>
          <p>ID: {user.id}</p>
          <p>Login: {user.login}</p>
        </div>
      ))}
      <Pagination currentPage={currentPage} totalPages={totalPages} onPageChange={setCurrentPage} />
    </div>
  );
};

export default UserList;
