import React, { useState, useEffect } from 'react';
import GitHubService from '../api/GitHubService';
import '../styles/RepoList.css';

const formatDate = (dateString) => new Date(dateString).toLocaleDateString();

const RepoList = React.memo(({ username }) => {
  const [repos, setRepos] = useState([]);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    const fetchRepos = async () => {
      setIsLoading(true);
      try {
        const userRepos = await GitHubService.getUserRepos(username);
        setRepos(userRepos);
      } catch (err) {
        setError(err.message || 'Failed to fetch user repositories');
      } finally {
        setIsLoading(false);
      }
    };

    if (username) {
      fetchRepos();
    }
  }, [username]);

  if (isLoading) {
    return <div className="loading">Loading...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <section className="repo-list">
      <h2>Repositories for {username}</h2>
      <ul>
        {repos.map((repo) => (
          <article key={repo.id} className="repo-item">
            <a href={repo.html_url} target="_blank" rel="noopener noreferrer" className="repo-link">
              {repo.name}
            </a>
            {repo.description && <p className="repo-description">{repo.description}</p>}
            {repo.language && <p className="repo-language">Language: {repo.language}</p>}
            <p className="repo-stars">Stars: {repo.stargazers_count}</p>
            <p className="repo-created-at">Created at: {formatDate(repo.created_at)}</p>
          </article>
        ))}
      </ul>
    </section>
  );
});

export default RepoList;
